import React from 'react'
import BH from "../../../../assets/img/home/bh.jpg";
import SP from "../../../../assets/img/home/SP.jpg";
import RJ from "../../../../assets/img/home/RJ.jpg";
import PA from "../../../../assets/img/home/PA.jpg";
import {gsap} from 'gsap'
import {ScrollTrigger} from 'gsap/ScrollTrigger'
import {useLayoutEffect} from 'react'

function Card() {

	useLayoutEffect(() => {
		gsap.registerPlugin(ScrollTrigger);
		gsap.to('#Card0', {
			x: 0,
			opacity: 1,
			scrollTrigger: {
				// markers: true,
				trigger: '#sec-trig',
				start: '200px',
				end: '280px',
				scrub: true,
			},
		})

		return () => {
			gsap.killTweensOf('.guarda-chuva')
		}

	}, [])
	
	useLayoutEffect(() => {
		gsap.registerPlugin(ScrollTrigger);
		gsap.to('#Card1', {
			x: 0,
			opacity: 1,
			scrollTrigger: {
				// markers: true,
				trigger: '#sec-trig',
				start: '230px',
				end: '300px',
				scrub: true,
			},
		})

		return () => {
			gsap.killTweensOf('.guarda-chuva')
		}

	}, [])



	let cidades = {
		"cidade1": {
			"nome": "Belo Horizonte",
			"imagem": BH,
			"descricao": "Here are the biggest enterprise technology acquisitions of 2021 so far, in reverse chronological order."
		},
		"cidade2": {
			"nome": "São Paulo",
			"imagem": SP,
			"descricao": "Here are the biggest enterprise technology acquisitions of 2021 so far, in reverse chronological order."
		},
		"cidade3": {
			"nome": "Rio de Janeiro",
			"imagem": RJ,
			"descricao": "Here are the biggest enterprise technology acquisitions of 2021 so far, in reverse chronological order."
		},
		"cidade4": {
			"nome": "Porto Alegre",
			"imagem": PA,
			"descricao": "Here are the biggest enterprise technology acquisitions of 2021 so far, in reverse chronological order."
		}
	}

	function repetirCard() {
		
		
		let i;
		i = 0;
		
		
		let cards = [];
		
		
		cards.push(
		 Object.keys(cidades).map((key) => {

			 let cidade = cidades[key];
			 return (
			  <div id={"Card" + i++} className="max-w-sm bg-white border border-gray-400 rounded-lg shadow ">
				  <div className="img-card bg-cover rounded-t-lg" style={{backgroundImage: `url(${cidade.imagem})`}}>
					  <div className="overlay"></div>
				  </div>
				  <div className="p-5 flex flex-col justify-center items-center">
					  <h5
					   className="mb-2 text-2xl font-bold tracking-tight text-gray-900 text-center">{cidade.nome}</h5>
					  <p className="mb-3 font-normal text-gray-700 text-center">{cidade.descricao}</p>
				  </div>
			  </div>
			 )
		 })
		);
		return cards;
	}

	return (
	 <>
		 {repetirCard()}
	 </>
	)
}

export default Card;
