import React from 'react'
import Oculos from '../../../../assets/img/home/oculos.png'
import {gsap} from 'gsap'
import {ScrollTrigger} from 'gsap/ScrollTrigger'
import {useLayoutEffect} from 'react'


function Item1() {

	useLayoutEffect(() => {
		gsap.registerPlugin(ScrollTrigger);
		gsap.to('.guarda-chuva', {
			x: -700,
			opacity: 0,
			scrollTrigger: {
				// markers: true,
				trigger: '#div-sec-oculos',
				start: '100px',
				end: '550px',
				scrub: true,
			},
		})

		return () => {
			gsap.killTweensOf('.guarda-chuva')
		}

	}, [])


	useLayoutEffect(() => {
		gsap.registerPlugin(ScrollTrigger);
		gsap.to('.texto-guarda-chuva', {
			x: 700,
			opacity: 0,
			scrollTrigger: {
				// markers: true,
				trigger: '#div-sec-oculos',
				start: '100px',
				end: '550px',
				scrub: true,
			},
		})

		return () => {
			gsap.killTweensOf('.texto-guarda-chuva')
		}

	}, [])

	return (
	 <div className="flex items-center justify-center flex-wrap mx-auto pb-20 ">
		 <div className="container">
			 <div id="div-sec-oculos" className="flex flex-col items-center justify-center">
				 <div className="flex gap-28 flex-row media-fd items-center justify-center">
					 <div className={'flex flex-col'}>
						 <h1 class="text-slate-900 font-bold text-4xl tracking-tight text-center relative z-10">
							 Com base nos dados de sua localização exata, a
						 </h1>

						 <div className="flex flex-col">
							 <div className="flex flex-row ">
								 <h1 className="margin-r-9 text-gradient text-4xl font-bold tracking-tighter">Weather
									 Connect</h1>
								 <h1
								  className="text-slate-900 font-bold text-4xl tracking-tight text-center relative z-10">
									 traz as informações e previsões
								 </h1>
							 </div>
							 <h1
							  className="text-slate-900 font-bold text-4xl tracking-tight text-center relative z-10">
								 climáticas do local em que você está.
							 </h1>
						 </div>
					 </div>
					 <img src={Oculos} className=" ml-20 cel:ml-0 object-contain img-size"></img>
				 </div>

			 </div>
		 </div>
	 </div>
	)
}

export default Item1;
