import { NavLink, useLocation } from 'react-router-dom';
import Foto from '../assets/img/logo.png';

function Header() {
	const location = useLocation();

	const isNavLinkActive = (path) => {
		return location.pathname === path;
	};


		var api_key = 'ff0e793530ac4053929bc43af63b8a0a';

		// reverse geocoding example (coordinates to address)
		// var latitude = '-19.862562845694825'; 
		// var longitude = '-43.951668066869246';
		// var query = latitude + ',' + longitude;

		// forward geocoding example (address to coordinate)
		var query = 'Rua Custódio de Melo, Indaiá, Belo Horizonte - MG, 31270-300, Brasil';
		// note: query needs to be URI encoded (see below)

		console.log(encodeURIComponent(query))
	
		var api_url = 'https://api.opencagedata.com/geocode/v1/json'

		var request_url = api_url
		+ '?'
		+ 'key=' + api_key
		+ '&q=' + encodeURIComponent(query)
		+ '&pretty=1'
		+ '&no_annotations=1';

		// see full list of required and optional parameters:
		// https://opencagedata.com/api#forward

		var request = new XMLHttpRequest();
		request.open('GET', request_url, true);

		request.onload = function() {
		// see full list of possible response codes:
		// https://opencagedata.com/api#codes

		if (request.status === 200){
		// Success!
		var data = JSON.parse(request.responseText);
		alert(data.results[0].formatted); // print the location
			console.log(data.results);

	} else if (request.status <= 500){
		// We reached our target server, but it returned an error

		console.log("unable to geocode! Response code: " + request.status);
		var data = JSON.parse(request.responseText);
		console.log('error msg: ' + data.status.message);
	} else {
		console.log("server error");
	}
	};

		request.onerror = function() {
		// There was a connection error of some sort
		console.log("unable to connect to server");
	};

		request.send();  // make the request

	
	return (
	 <header>
		 <nav className="bg-gray-200 py-3 shadow-xl">
			 <div className="flex items-center justify-center flex-wrap mx-auto">
				 <div className="container">
					 <div className="container">
						 <div className="flex flex-row justify-between items-center mx-auto">
							 <div className="flex flex-row items-center">
								 <img src={Foto} alt="Weather Connect Logo" className="w-10 h-10 mr-2" />
								 <div className="text-gradient">
									 <span className="text-2xl font-bold text-slate-700 tracking-tighter">Weather Connect</span>
								 </div>
							 </div>
							 <div className="flex flex-row items-center">
								 <ul className="font-semibold flex lg:flex-row lg:space-x-8 lg:mt-0">
									 <li>
										 <NavLink
										  to="/"
										  activeClassName="text-slate-500"
										  className={`text-slate800 hover:text-slate-500 ${isNavLinkActive('/') ? 'text-gradient font-bold' : ''}`}
										 >
											 Home
										 </NavLink>
									 </li>
									 <li>
										 <NavLink
										  to="/clima"
										  activeClassName="text-slate-500"
										  className={`text-slate800 hover:text-slate-500 ${isNavLinkActive('/clima') ? 'text-gradient font-bold' : ''}`}
										 >
											 Clima
										 </NavLink>
									 </li>
									 <li>
										 <NavLink
										  to="/astronomia"
										  activeClassName="text-slate-500"
										  className={`text-slate800 hover:text-slate-500 ${isNavLinkActive('/astronomia') ? 'text-gradient font-bold' : ''}`}
										 >
											 Astronomia
										 </NavLink>
									 </li>
									 <li>
										 <NavLink
										  to="/sol"
										  activeClassName="text-slate-500"
										  className={`text-slate800 hover:text-slate-500 ${isNavLinkActive('/sol') ? 'text-gradient font-bold' : ''}`}
										 >
											 Sol
										 </NavLink>
									 </li>
									 <li>
										 <NavLink
										  to="/mares"
										  activeClassName="text-slate-500"
										  className={`text-slate800 hover:text-slate-500 ${isNavLinkActive('/mares') ? 'text-gradient font-bold' : ''}`}
										 >
											 Marés
										 </NavLink>
									 </li>
									 <li className="flex items-center">
										 <a
										  href="https://github.com/GabrielAm0/WeatherConnect"
										  target="_blank"
										  className="ml-1 block text-slate800 hover:text-slate-500"
										 >
											 <span className="sr-only">Tailwind CSS on GitHub</span>
											 <svg viewBox="0 0 16 16" className="w-5 h-5" fill="currentColor" aria-hidden="true">
												 <path
												  d="M8 0C3.58 0 0 3.58 0 8c0 3.54 2.29 6.53 5.47 7.59.4.07.55-.17.55-.38 0-.19-.01-.82-.01-1.49-2.01.37-2.53-.49-2.69-.94-.09-.23-.48-.94-.82-1.13-.28-.15-.68-.52-.01-.53.63-.01 1.08.58 1.23.82.72 1.21 1.87.87 2.33.66.07-.52.28-.87.51-1.07-1.78-.2-3.64-.89-3.64-3.95 0-.87.31-1.59.82-2.15-.08-.2-.36-1.02.08-2.12 0 0 .67-.21 2.2.82.64-.18 1.32-.27 2-.27.68 0 1.36.09 2 .27 1.53-1.04 2.2-.82 2.2-.82.44 1.1.16 1.92.08 2.12.51.56.82 1.27.82 2.15 0 3.07-1.87 3.75-3.65 3.95.29.25.54.73.54 1.48 0 1.07-.01 1.93-.01 2.2 0 .21.15.46.55.38A8.013 8.013 0 0016 8c0-4.42-3.58-8-8-8z"
												 ></path>
											 </svg>
										 </a>
									 </li>
								 </ul>
							 </div>
						 </div>
					 </div>
				 </div>
			 </div>
		 </nav>
	 </header>
	);
}

export default Header;
