	return (
	 <>
		 <div className="flex items-center justify-center flex-wrap mx-auto pb-20">
			 <div className="container">
				 <div className="flex flex-col items-center justify-center">
					 <div className="flex gap-28 flex-row media-fd items-center justify-center">

						 <div class="max-w-sm bg-white border border-gray-400 rounded-lg shadow ">
							 <img class="img-size rounded-t-lg" src="../../../../assets/img/home/bh.jpg" alt=""></img>
							 <div class="p-5">
								 <a href="#">
									 <h5
									  class="mb-2 text-2xl font-bold tracking-tight text-gray-900 dark:text-white">Noteworthy
										 technology acquisitions 2021</h5>
								 </a>
								 <p class="mb-3 font-normal text-gray-700 dark:text-gray-400">Here are the biggest
									 enterprise technology acquisitions of 2021 so far, in reverse chronological
									 order.</p>
								 <a href="#"
								    class="inline-flex items-center px-3 py-2 text-sm font-medium text-center text-white bg-blue-700 rounded-lg hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">
									 Read more
									 <svg class="rtl:rotate-180 w-3.5 h-3.5 ms-2" aria-hidden="true"
									      xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 14 10">
										 <path stroke="currentColor" stroke-linecap="round" stroke-linejoin="round"
										       stroke-width="2" d="M1 5h12m0 0L9 1m4 4L9 9"/>
									 </svg>
								 </a>
							 </div>
						 </div>

					 </div>
				 </div>
			 </div>
		 </div>
	 </>
	)
}

export default Item1;
