import React from 'react'
import BH from "../../../../assets/img/home/bh.jpg";
import SP from "../../../../assets/img/home/SP.jpg";
import RJ from "../../../../assets/img/home/RJ.jpg";
import PA from "../../../../assets/img/home/PA.jpg";

function Card() {

	let cidades = {
		"cidade1": {
			"nome": "Belo Horizonte",
			"imagem": BH,
			"descricao": "Here are the biggest enterprise technology acquisitions of 2021 so far, in reverse chronological order."
		},
		"cidade2": {
			"nome": "São Paulo",
			"imagem": SP,
			"descricao": "Here are the biggest enterprise technology acquisitions of 2021 so far, in reverse chronological order."
		},
		"cidade3": {
			"nome": "Rio de Janeiro",
			"imagem": RJ,
			"descricao": "Here are the biggest enterprise technology acquisitions of 2021 so far, in reverse chronological order."
		},
		"cidade4": {
			"nome": "Porto Alegre",
			"imagem": PA,
			"descricao": "Here are the biggest enterprise technology acquisitions of 2021 so far, in reverse chronological order."
		}
	}

	function repetirCard() {
		let cards = [];
		for (let i = 0; i < 4; i++) {
			cards.push(
			 Object.keys(cidades).map((key) => {
				 let cidade = cidades[key];
				 return (
				  <div key={i} className="max-w-sm bg-white border border-gray-400 rounded-lg shadow ">
					  <img className="bg-cover rounded-t-lg" src={cidade.imagem} alt=""></img>
					  <div className="p-5 flex flex-col justify-center items-center">
						  <h5 className="mb-2 text-2xl font-bold tracking-tight text-gray-900 text-center">{cidade.nome}</h5>
						  <p className="mb-3 font-normal text-gray-700 text-center">{cidade.descricao}</p>
					  </div>
				  </div>
				 )
			 })
			);
		}
		return cards;
	}

	return (
	 <>
		 {repetirCard()}
	 </>
	)
}

export default Card;
