import {NavLink, useLocation} from 'react-router-dom';
import Foto from '../assets/img/logo.png';
import {useState} from 'react';

function Header() {
	const location = useLocation();

	const isNavLinkActive = (path) => {
		return location.pathname === path;
	};

	const [isOpen, setIsOpen] = useState(false);

	const toggleMenu = () => {
		setIsOpen(!isOpen);
	};

	return (
	 <header className="fixed w-full top-0 z-50">
		 <nav className="bg-gray-100 py-3 header">
			 <div className="flex items-center justify-between px-4 py-2">
				 <div className="flex items-center">
					 <img src={Foto} alt="Weather Connect Logo" className="w-10 h-10 mr-2"/>
					 <div className="text-gradient">
						 <span className="text-2xl font-bold text-slate-700 tracking-tighter">Weather Connect</span>
					 </div>
				 </div>
				 <div className="lg:hidden">
					 <button type="button"
					         className="block text-gray-500 hover:text-white focus:text-white focus:outline-none"
					         onClick={toggleMenu}>
						 <svg className="h-6 w-6 fill-current" viewBox="0 0 24 24">
							 {isOpen ? (
							  <path fillRule="evenodd" clipRule="evenodd"
							        d="M18.278 16.864a1 1 0 01-1.414 1.414l-4.829-4.828-4.828 4.828a1 1 0 01-1.414-1.414l4.828-4.829-4.828-4.828a1 1 0 011.414-1.414l4.829 4.828 4.828-4.828a1 1 0 011.414 1.414l-4.828 4.829 4.828 4.828z"/>
							 ) : (
							  <path fillRule="evenodd" clipRule="evenodd"
							        d="M4 5h16a1 1 0 010 2H4a1 1 0 110-2zm0 6h16a1 1 0 010 2H4a1 1 0 010-2zm0 6h16a1 1 0 010 2H4a1 1 0 010-2z"/>
							 )}
						 </svg>
					 </button>
				 </div>
				 <div className={`lg:flex ${isOpen ? 'block' : 'hidden'}`}>
					 <NavLink to="/home"
					          className={`block lg:inline-block mt-4 lg:mt-0 lg:ml-6 text-gray-500 hover:text-gray-900 ${isNavLinkActive('/home') ? 'text-gradient font-extrabold' : ''}`}>Home</NavLink>
					 <NavLink to="/clima"
					          className="block lg:inline-block mt-4 lg:mt-0 lg:ml-6 text-gray-500 hover:text-gray-900">Clima</NavLink>
					 <NavLink to="/astronomia"
					          className="block lg:inline-block mt-4 lg:mt-0 lg:ml-6 text-gray-500 hover:text-gray-900">Astronomia</NavLink>
					 <NavLink to="/sol"
					          className="block lg:inline-block mt-4 lg:mt-0 lg:ml-6 text-gray-500 hover:text-gray-900">Sol</NavLink>
					 <NavLink to="/mares"
					          className="block lg:inline-block mt-4 lg:mt-0 lg:ml-6 text-gray-500 hover:text-gray-900">Mar√©s</NavLink>
				 </div>
			 </div>
		 </nav>
	 </header>
	);
}

export default Header;
